n&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        String msg = &lt;span class=&#34;keyword&#34;&gt;new&lt;/span&gt; String(b, &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;, len);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        System.out.println(msg);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        OutputStream os = server.getOutputStream();&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        os.write(&lt;span class=&#34;string&#34;&gt;&amp;quot;服务器向客户端发送的数据&amp;quot;&lt;/span&gt;.getBytes());&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;comment&#34;&gt;// 7.关闭资源.&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        os.close();&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        is.close();&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        server.close();&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
&lt;p&gt;2.创建客户端&lt;/p&gt;
&lt;figure class=&#34;highlight java&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;8&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;9&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;10&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;11&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;12&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;13&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;14&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;15&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;16&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;17&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;18&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;19&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;import&lt;/span&gt; java.io.InputStream;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;import&lt;/span&gt; java.io.OutputStream;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;import&lt;/span&gt; java.net.Socket;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;public&lt;/span&gt; &lt;span class=&#34;class&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;class&lt;/span&gt; &lt;span class=&#34;title&#34;&gt;ClientTets&lt;/span&gt; &lt;/span&gt;&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;function&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;public&lt;/span&gt; &lt;span class=&#34;keyword&#34;&gt;static&lt;/span&gt; &lt;span class=&#34;keyword&#34;&gt;void&lt;/span&gt; &lt;span class=&#34;title&#34;&gt;main&lt;/span&gt;&lt;span class=&#34;params&#34;&gt;(String[] args)&lt;/span&gt; &lt;span class=&#34;keyword&#34;&gt;throws&lt;/span&gt; Exception &lt;/span&gt;&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        System.out.println(&lt;span class=&#34;string&#34;&gt;&amp;quot;客户端:发送数据&amp;quot;&lt;/span&gt;);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        Socket client = &lt;span class=&#34;keyword&#34;&gt;new&lt;/span&gt; Socket(&lt;span class=&#34;string&#34;&gt;&amp;quot;localhost&amp;quot;&lt;/span&gt;, &lt;span class=&#34;number&#34;&gt;4444&lt;/span&gt;);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        OutputStream os = client.getOutputStream();&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        os.write(&lt;span class=&#34;string&#34;&gt;&amp;quot;客户端向服务器发送的数据&amp;quot;&lt;/span&gt;.getBytes());&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        InputStream is = client.getInputStream();&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;keyword&#34;&gt;byte&lt;/span&gt;[] b = &lt;span class=&#34;keyword&#34;&gt;new&lt;/span&gt; &lt;span class=&#34;keyword&#34;&gt;byte&lt;/span&gt;[&lt;span class=&#34;number&#34;&gt;100&lt;/span&gt;];&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;keyword&#34;&gt;int&lt;/span&gt; len = is.read(b);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        System.out.println(&lt;span class=&#34;keyword&#34;&gt;new&lt;/span&gt; String(b, &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;, len));&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        is.close();&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        os.close();&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        client.close();&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
&lt;p&gt;运行结果如下，首先创建了服务器，然后创建客户端连接服务器，客户端先向服务器发送数据，然后服务器再回写客户端数据&lt;/p&gt;
&lt;div align=center&gt;&lt;img src=&#34;https://cdn.jsdelivr.net/gh/sunyuzero/cdn/java/java45.png&#34; width=600&gt;&lt;/div&gt;

 &lt;center&gt;&lt;font face=&#34;黑体&#34; size=8&gt; JAVA文件操作&amp;IO流 &lt;/font&gt;&lt;/center&gt;


&lt;h1 id=&#34;java文件操作&#34;&gt;&lt;a href=&#34;#java文件操作&#34; class=&#34;headerlink&#34; title=&#34;java文件操作&#34;&gt;&lt;/a&gt;java文件操作&lt;/h1&gt;&lt;h2 id=&#34;File类&#34;&gt;&lt;a href=&#34;#File类&#34; class=&#34;headerlink&#34; title=&#34;File类&#34;&gt;&lt;/a&gt;File类&lt;/h2&gt;&lt;p&gt;File类是对文件和文件夹操作的一个类，它有以下几个功能：&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;创建文件/文件夹&lt;/li&gt;
&lt;li&gt;删除文件/文件夹&lt;/li&gt;
&lt;li&gt;查找文件/文件夹（判断是否存在）&lt;/li&gt;
&lt;li&gt;获取文件/文件夹&lt;/li&gt;
&lt;li&gt;遍历文件夹&lt;/li&gt;
&lt;li&gt;获取文件大小&lt;br&gt;由于不同操作系统的分隔符可能不同，所以File类有以下成员变量用于表示分隔符：&lt;/li&gt;
&lt;li&gt;默认名称分隔符：File.separatorr，即”&amp;quot;&lt;/li&gt;
&lt;li&gt;默认路径分隔符：File.pathSeparator，即”;”&lt;/li&gt;
&lt;/ul&gt;
&lt;h3 id=&#34;File类的创建&#34;&gt;&lt;a href=&#34;#File类的创建&#34; class=&#34;headerlink&#34; title=&#34;File类的创建&#34;&gt;&lt;/a&gt;File类的创建&lt;/h3&gt;&lt;p&gt;File的构造方法有三种：&lt;br&gt;一个参数：其参数是文件的路径，创建File的对象，其参数表示的文件可以不存在；&lt;br&gt;两个参数：前者表示父路径，后者表示子路径，组合起来表示文件路径；&lt;br&gt;两个参数：前者表示父类文件，后者表示子类文件；&lt;br&gt;当我们直接打印File对象时，将会打印出file的路径，即传递的参数：&lt;/p&gt;
&lt;figure class=&#34;highlight java&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;8&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;9&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;File file1 = &lt;span class=&#34;keyword&#34;&gt;new&lt;/span&gt; File(&lt;span class=&#34;string&#34;&gt;&amp;quot;D:/IDEA/FileMode/1.txt&amp;quot;&lt;/span&gt;);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;System.out.println(file1);       &lt;span class=&#34;comment&#34;&gt;//D:\IDEA\FileMode\1.txt&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;File file2 = &lt;span class=&#34;keyword&#34;&gt;new&lt;/span&gt; File(&lt;span class=&#34;string&#34;&gt;&amp;quot;D:/IDEA/FileMode/&amp;quot;&lt;/span&gt;，&lt;span class=&#34;string&#34;&gt;&amp;quot;1.txt&amp;quot;&lt;/span&gt;);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;System.out.println(file2);       &lt;span class=&#34;comment&#34;&gt;//D:\IDEA\FileMode\1.txt&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;File file3 = &lt;span class=&#34;keyword&#34;&gt;new&lt;/span&gt; File(&lt;span class=&#34;string&#34;&gt;&amp;quot;D:/IDEA/FileMode/&amp;quot;&lt;/span&gt;);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;File file4 = &lt;span class=&#34;keyword&#34;&gt;new&lt;/span&gt; File(file1,&lt;span class=&#34;string&#34;&gt;&amp;quot;1.txt&amp;quot;&lt;/span&gt;);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;System.out.println(file4);       &lt;span class=&#34;comment&#34;&gt;//D:\IDEA\FileMode\1.txt &lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;

