&lt;figure class=&#34;highlight cpp&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;8&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;9&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;built_in&#34;&gt;fabs&lt;/span&gt;(&lt;span class=&#34;keyword&#34;&gt;double&lt;/span&gt; x);  &lt;span class=&#34;comment&#34;&gt;//取x的绝对值&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;built_in&#34;&gt;floor&lt;/span&gt;(&lt;span class=&#34;keyword&#34;&gt;double&lt;/span&gt; x);  &lt;span class=&#34;comment&#34;&gt;//向上取整&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;built_in&#34;&gt;ceil&lt;/span&gt;(&lt;span class=&#34;keyword&#34;&gt;double&lt;/span&gt; x);  &lt;span class=&#34;comment&#34;&gt;//向下取整&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;built_in&#34;&gt;pow&lt;/span&gt;(&lt;span class=&#34;keyword&#34;&gt;double&lt;/span&gt; r,&lt;span class=&#34;keyword&#34;&gt;double&lt;/span&gt; p) &lt;span class=&#34;comment&#34;&gt;//返回r的p次方&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;built_in&#34;&gt;sqrt&lt;/span&gt;(&lt;span class=&#34;keyword&#34;&gt;double&lt;/span&gt; x);  &lt;span class=&#34;comment&#34;&gt;//返回x的算术平方根&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;built_in&#34;&gt;log&lt;/span&gt;(&lt;span class=&#34;keyword&#34;&gt;double&lt;/span&gt; x);  &lt;span class=&#34;comment&#34;&gt;//返回ln(x)&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;built_in&#34;&gt;sin&lt;/span&gt;(&lt;span class=&#34;keyword&#34;&gt;double&lt;/span&gt; x);  &lt;span class=&#34;comment&#34;&gt;//返回正弦值，要求x为弧度制&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;built_in&#34;&gt;acos&lt;/span&gt;(&lt;span class=&#34;keyword&#34;&gt;double&lt;/span&gt; x);  &lt;span class=&#34;comment&#34;&gt;//返回反余弦值，pi=acos(-1);&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;round(&lt;span class=&#34;keyword&#34;&gt;double&lt;/span&gt; x);  &lt;span class=&#34;comment&#34;&gt;//对x进行四舍五入取整&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
&lt;h1 id=&#34;memset函数&#34;&gt;&lt;a href=&#34;#memset函数&#34; class=&#34;headerlink&#34; title=&#34;memset函数&#34;&gt;&lt;/a&gt;memset函数&lt;/h1&gt;&lt;p&gt;memset用于对数组中的每一个元素赋予相同的值（0或-1）,下面的这个函数即是对数组a的元素全部赋予0的操作&lt;br&gt;memset(a,0,sizeof(a));&lt;/p&gt;
